package com.iu.phone;

public class Galaxy {
	private int price;
	private String color;
	
	public Galaxy(int price) {
		this.price = price;
	}
	
	public Galaxy() {
		// TODO Auto-generated constructor stub
	}
	
	public int getPrice() {
		return price;
	}
	public void setPrice(int price) {
		this.price = price;
	}
	public String getColor() {
		return color;
	}
	public void setColor(String color) {
		this.color = color;
	}
}